import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const WEATHER_API_KEY = '445f1860e609a11c24d90f09fa0cc0cb'; // Replace this!

const Todo = () => {
  const location = useLocation();
  const username = location.state?.username;

  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState('');
  const [taskType, setTaskType] = useState('indoor');
  const [taskCity, setTaskCity] = useState('');
  const [loadingTaskIndex, setLoadingTaskIndex] = useState(null); // track which task is loading
  const [error, setError] = useState('');

  useEffect(() => {
    if (username) {
      const storedTasks = JSON.parse(localStorage.getItem(`tasks_${username}`)) || [];
      setTasks(storedTasks);
    }
  }, [username]);

  const fetchWeather = async (city, index) => {
    try {
      setLoadingTaskIndex(index);
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${WEATHER_API_KEY}&units=metric`
      );
      const weather = response.data.weather[0].description;
      const temp = response.data.main.temp;

      const updatedTasks = [...tasks];
      updatedTasks[index].weather = `${weather}, ${temp}°C`;
      setTasks(updatedTasks);
    } catch (err) {
      setError('Failed to fetch weather');
    } finally {
      setLoadingTaskIndex(null);
    }
  };

  const handleAddTask = () => {
    if (taskText.trim() === '') {
      alert('Please enter a task');
      return;
    }

    if (taskType === 'outdoor' && taskCity.trim() === '') {
      alert('Please enter your city for outdoor task');
      return;
    }

    const newTask = {
      text: taskText,
      type: taskType,
      city: taskType === 'outdoor' ? taskCity : '',
      weather: null,
    };

    const updatedTasks = [...tasks, newTask];
    localStorage.setItem(`tasks_${username}`, JSON.stringify(updatedTasks));
    setTasks(updatedTasks);

    // Reset
    setTaskText('');
    setTaskCity('');
    setTaskType('indoor');
    setError('');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Welcome, {username}!</h2>

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          placeholder="Enter your task"
          style={{ padding: '5px', width: '60%' }}
        />

        <select
          value={taskType}
          onChange={(e) => setTaskType(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px' }}
        >
          <option value="indoor">Indoor</option>
          <option value="outdoor">Outdoor</option>
        </select>

        {taskType === 'outdoor' && (
          <input
            type="text"
            value={taskCity}
            onChange={(e) => setTaskCity(e.target.value)}
            placeholder="Enter city"
            style={{ marginLeft: '10px', padding: '5px' }}
          />
        )}

        <button onClick={handleAddTask} style={{ marginLeft: '10px', padding: '5px 10px' }}>
          Add Task
        </button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h3>Your Tasks:</h3>
      {tasks.length === 0 ? (
        <p>No tasks yet.</p>
      ) : (
        <ul>
          {tasks.map((task, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              {task.text} ({task.type})
              {task.type === 'outdoor' && (
                <>
                  <span> – City: {task.city}</span>
                  {task.weather ? (
                    <span> – Weather: {task.weather}</span>
                  ) : (
                    <button
                      onClick={() => fetchWeather(task.city, index)}
                      style={{ marginLeft: '10px' }}
                      disabled={loadingTaskIndex === index}
                    >
                      {loadingTaskIndex === index ? 'Loading...' : 'Get Weather'}
                    </button>
                  )}
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Todo;
