// action types
export const ADD_TASK = 'ADD_TASK';
export const DELETE_TASK = 'DELETE_TASK';
export const SET_WEATHER = 'SET_WEATHER';
export const SET_ERROR = 'SET_ERROR';
export const LOGIN = 'LOGIN';
export const LOGOUT = 'LOGOUT';
import axios from 'axios';

// action creators
export const addTask = (task) => ({ type: ADD_TASK, payload: task });
export const deleteTask = (id) => ({ type: DELETE_TASK, payload: id });

export const fetchWeather = (location) => async (dispatch) => {
  try {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=YOUR_API_KEY`);
    dispatch({ type: SET_WEATHER, payload: response.data });
  } catch (error) {
    dispatch({ type: SET_ERROR, payload: error.message });
  }
};

export const login = (user) => ({ type: LOGIN, payload: user });
export const logout = () => ({ type: LOGOUT });
