import React, { useState, useEffect } from 'react';

const Todo = () => {
  const [task, setTask] = useState('');
  const [taskType, setTaskType] = useState('');
  const [userName, setUserName] = useState('');
  const [tasks, setTasks] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Load user info and tasks from localStorage
  useEffect(() => {
    const storedUserName = localStorage.getItem('userName');
    if (storedUserName) {
      setUserName(storedUserName);
      setIsLoggedIn(true);
      const storedTasks = JSON.parse(localStorage.getItem(storedUserName)) || [];
      setTasks(storedTasks);
    }
  }, []);

  const handleLogin = () => {
    if (!userName) {
      alert('Please enter a username to sign in!');
      return;
    }

    const storedTasks = JSON.parse(localStorage.getItem(userName));
    if (storedTasks) {
      // User exists and has tasks, so log them in
      setIsLoggedIn(true);
      setTasks(storedTasks);
      localStorage.setItem('userName', userName); // Save username for session persistence
    } else {
      // New user, no tasks found
      setIsLoggedIn(false);
      setTasks([]);
      localStorage.setItem('userName', userName); // Save new username
      alert('New user created!');
    }
  };

  const handleAddTask = () => {
    if (!task || !taskType) {
      alert('Please enter a task and select its type!');
      return;
    }

    const newTask = { task, taskType };
    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);

    // Save tasks to localStorage under the user's name
    localStorage.setItem(userName, JSON.stringify(updatedTasks));

    // Clear input fields
    setTask('');
    setTaskType('');
  };

  const handleLogout = () => {
    // Clear user session
    setIsLoggedIn(false);
    setUserName('');
    setTasks([]);
    localStorage.removeItem('userName');
  };

  return (
    <div>
      <h1>Todo App</h1>

      {/* Sign-in Section */}
      {!isLoggedIn ? (
        <div>
          <input
            type="text"
            placeholder="Enter your name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <button onClick={handleLogin}>Sign In</button>
        </div>
      ) : (
        <div>
          <h2>Welcome, {userName}!</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}

      {/* Task Input Section */}
      {isLoggedIn && (
        <div>
          <input
            type="text"
            placeholder="Enter your task"
            value={task}
            onChange={(e) => setTask(e.target.value)}
          />

          <select value={taskType} onChange={(e) => setTaskType(e.target.value)}>
            <option value="">Select task type</option>
            <option value="indoor">Indoor</option>
            <option value="outdoor">Outdoor</option>
          </select>

          <button onClick={handleAddTask}>Add Task</button>

          <h2>Your Tasks:</h2>
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>
                {task.task} ({task.taskType})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Todo;
