import React, { useState } from 'react';

const TaskList = ({ tasks, onDelete, onFetchWeather, loadingIndex, onEdit }) => {
  const [editIndex, setEditIndex] = useState(null);
  const [editTask, setEditTask] = useState({});

  const priorityColor = {
    High: 'red',
    Medium: 'orange',
    Low: 'green',
  };

  const priorityOrder = { High: 1, Medium: 2, Low: 3 };
  const sortedTasks = [...tasks].sort((a, b) => priorityOrder[a.priority] - priorityOrder[b.priority]);

  const startEdit = (task, index) => {
    setEditIndex(index);
    setEditTask({ ...task });
  };

  const handleSave = () => {
    onEdit(editTask, editIndex);
    setEditIndex(null);
    setEditTask({});
  };

  return (
    <ul style={{ padding: 0, listStyle: 'none', maxWidth: '700px' }}>
      {sortedTasks.length === 0 ? (
        <p style={{ color: '#555' }}>No tasks yet.</p>
      ) : (
        sortedTasks.map((task, index) => {
          const isEditing = index === editIndex;

          return (
            <li
              key={index}
              style={{
                backgroundColor: '#ffffff',
                padding: '15px',
                borderRadius: '8px',
                marginBottom: '15px',
                boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
              }}
            >
              {isEditing ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  <input
                    type="text"
                    value={editTask.text}
                    onChange={(e) => setEditTask({ ...editTask, text: e.target.value })}
                  />
                  <select
                    value={editTask.type}
                    onChange={(e) =>
                      setEditTask({ ...editTask, type: e.target.value, city: e.target.value === 'indoor' ? '' : editTask.city })
                    }
                  >
                    <option value="indoor">Indoor</option>
                    <option value="outdoor">Outdoor</option>
                  </select>

                  {editTask.type === 'outdoor' && (
                    <input
                      type="text"
                      value={editTask.city}
                      onChange={(e) => setEditTask({ ...editTask, city: e.target.value })}
                    />
                  )}

                  <select
                    value={editTask.priority}
                    onChange={(e) => setEditTask({ ...editTask, priority: e.target.value })}
                  >
                    <option value="High">High</option>
                    <option value="Medium">Medium</option>
                    <option value="Low">Low</option>
                  </select>

                  <div>
                    <button onClick={handleSave} style={buttonStyles.save}>Save</button>
                    <button onClick={() => setEditIndex(null)} style={buttonStyles.cancel}>Cancel</button>
                  </div>
                </div>
              ) : (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <strong>{task.text}</strong> ({task.type}) – 
                    <span style={{ marginLeft: '6px', color: priorityColor[task.priority] }}>
                      {task.priority} Priority
                    </span>
                    {task.type === 'outdoor' && (
                      <>
                        <span> – City: {task.city}</span>
                        {task.weather ? (
                          <span> – Weather: {task.weather}</span>
                        ) : (
                          <button
                            onClick={() => onFetchWeather(task.city, index)}
                            style={buttonStyles.weather}
                            disabled={loadingIndex === index}
                          >
                            {loadingIndex === index ? 'Loading...' : 'Get Weather'}
                          </button>
                        )}
                      </>
                    )}
                  </div>
                  <div>
                    <button onClick={() => startEdit(task, index)} style={buttonStyles.edit}>Edit</button>
                    <button onClick={() => onDelete(index)} style={buttonStyles.delete}>Delete</button>
                  </div>
                </div>
              )}
            </li>
          );
        })
      )}
    </ul>
  );
};

const buttonStyles = {
  edit: {
    backgroundColor: '#4299e1',
    color: 'white',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '5px',
    cursor: 'pointer',
    marginRight: '8px',
  },
  delete: {
    backgroundColor: '#e53e3e',
    color: 'white',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  save: {
    backgroundColor: '#38a169',
    color: 'white',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '5px',
    cursor: 'pointer',
    marginRight: '8px',
  },
  cancel: {
    backgroundColor: '#a0aec0',
    color: 'white',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  weather: {
    marginLeft: '10px',
    padding: '5px 10px',
    backgroundColor: '#81e6d9',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  }
};

export default TaskList;
