import React from 'react';

const TaskList = ({ tasks, onDelete, onFetchWeather, loadingIndex }) => {
  return (
    <ul style={{ padding: 0, listStyle: 'none', maxWidth: '700px' }}>
      {tasks.length === 0 ? (
        <p style={{ color: '#555' }}>No tasks yet.</p>
      ) : (
        tasks.map((task, index) => (
          <li
            key={index}
            style={{
              backgroundColor: '#ffffff',
              padding: '15px',
              borderRadius: '8px',
              marginBottom: '15px',
              boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
            }}
          >
            <div>
              <strong>{task.text}</strong> ({task.type})
              {task.type === 'outdoor' && (
                <>
                  <span> – City: {task.city}</span>
                  {task.weather ? (
                    <span> – Weather: {task.weather}</span>
                  ) : (
                    <button
                      onClick={() => onFetchWeather(task.city, index)}
                      style={{
                        marginLeft: '10px',
                        padding: '5px 10px',
                        backgroundColor: '#81e6d9',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                      }}
                      disabled={loadingIndex === index}
                    >
                      {loadingIndex === index ? 'Loading...' : 'Get Weather'}
                    </button>
                  )}
                </>
              )}
            </div>
            <button
              onClick={() => onDelete(index)}
              style={{
                marginLeft: '10px',
                backgroundColor: '#e53e3e',
                color: 'white',
                border: 'none',
                padding: '6px 12px',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Delete
            </button>
          </li>
        ))
      )}
    </ul>
  );
};

export default TaskList;
