import { ADD_TASK, DELETE_TASK, SET_WEATHER, SET_ERROR, LOGIN, LOGOUT } from './actions';

// tasks reducer
const initialTasksState = { tasks: [] };
const tasksReducer = (state = initialTasksState, action) => {
  switch (action.type) {
    case ADD_TASK:
      return { ...state, tasks: [...state.tasks, action.payload] };
    case DELETE_TASK:
      return { ...state, tasks: state.tasks.filter(task => task.id !== action.payload) };
    default:
      return state;
  }
};

// weather reducer
const initialWeatherState = { data: null, error: null };
const weatherReducer = (state = initialWeatherState, action) => {
  switch (action.type) {
    case SET_WEATHER:
      return { ...state, data: action.payload, error: null };
    case SET_ERROR:
      return { ...state, error: action.payload };
    default:
      return state;
  }
};

// auth reducer
const initialAuthState = { isAuthenticated: false, user: null };
const authReducer = (state = initialAuthState, action) => {
  switch (action.type) {
    case LOGIN:
      return { ...state, isAuthenticated: true, user: action.payload };
    case LOGOUT:
      return { ...state, isAuthenticated: false, user: null };
    default:
      return state;
  }
};

export default { tasks: tasksReducer, weather: weatherReducer, auth: authReducer };
