import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const Todo = () => {
  const location = useLocation();
  const username = location.state?.username;

  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState('');
  const [taskType, setTaskType] = useState('indoor'); // 'indoor' or 'outdoor'

  useEffect(() => {
    if (username) {
      const storedTasks = JSON.parse(localStorage.getItem(`tasks_${username}`)) || [];
      setTasks(storedTasks);
    }
  }, [username]);

  const handleAddTask = () => {
    if (taskText.trim() === '') {
      alert("Please enter a task");
      return;
    }

    const newTask = { text: taskText, type: taskType };
    const updatedTasks = [...tasks, newTask];

    // Save to localStorage
    localStorage.setItem(`tasks_${username}`, JSON.stringify(updatedTasks));

    // Update local state
    setTasks(updatedTasks);
    setTaskText('');
    setTaskType('indoor');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Welcome, {username}!</h2>

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          placeholder="Enter your task"
          style={{ padding: '5px', width: '60%' }}
        />

        <select
          value={taskType}
          onChange={(e) => setTaskType(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px' }}
        >
          <option value="indoor">Indoor</option>
          <option value="outdoor">Outdoor</option>
        </select>

        <button onClick={handleAddTask} style={{ marginLeft: '10px', padding: '5px 10px' }}>
          Add Task
        </button>
      </div>

      <h3>Your Tasks:</h3>
      {tasks.length === 0 ? (
        <p>No tasks yet.</p>
      ) : (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              {task.text} ({task.type})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Todo;
